    private void login (){
       //lblNome.setText("Nome: "+ txtNome.getText()); //criei a função login 
                                                    //private void sem argumentos
                                                    
       if ( new String(lblPass.getPassword()).equals(key) && user.equals(txtNome.getText()) )
       {
         JanelaPrivada janelaPrivada = new JanelaPrivada();
         janelaPrivada.setVisible(true);
         this.dispose();
       }else{
           
       
       JOptionPane.showMessageDialog(this,"Nome: "+txtNome.getText()
                + "\nSenha: "+ new String(lblPass.getPassword())  );
       JOptionPane.showMessageDialog(this,"SENHA OU USUARIO INCORRECTOS","ERRO",JOptionPane.ERROR_MESSAGE );
    
            }  
    }
  
