    private void chkPassActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_chkPassActionPerformed
        // TODO add your handling code here:
        if (chkPass.isSelected()) {
            lblPass.setEchoChar ('\u0000');
            jLabel4.setIcon(new javax.swing.ImageIcon(getClass().getResource("/pincipal/ocultarSenha.png"))); 
            chkPass.setText("Ocultar senha");
       }else {
            lblPass.setEchoChar (senha);
            jLabel4.setIcon(new javax.swing.ImageIcon(getClass().getResource("/pincipal/exibirSenha.png"))); 
            chkPass.setText("Mostrar senha");
        }
    }//GEN-LAST:event_chkPassActionPerformed

